mapper.select:
select
id, shop_id, user_id, mobile, coupon_id, add_time, use_time, status
from coupon_accept


        and id = #{id}


        and shop_id = #{shopId}


        and user_id = #{userId}


        and mobile = #{mobile}


        and coupon_id = #{couponId}


        and add_time = #{addTime}


        and use_time = #{useTime}


        and status = #{status}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from coupon_accept


        and id = #{id}


        and shop_id = #{shopId}


        and user_id = #{userId}


        and mobile = #{mobile}


        and coupon_id = #{couponId}


        and add_time = #{addTime}


        and use_time = #{useTime}


        and status = #{status}
