mapper.select:
select
id, coupon_id, shop_id, type, title, target_money, discount_money, end_time, add_time, number, status
from coupon


        and id = #{id}


        and coupon_id = #{couponId}


        and shop_id = #{shopId}


        and type = #{type}


        and title = #{title}


        and target_money = #{targetMoney}


        and discount_money = #{discountMoney}


        and end_time = #{endTime}


        and add_time = #{addTime}


        and number = #{number}


        and status = #{status}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from coupon


        and id = #{id}


        and coupon_id = #{couponId}


        and shop_id = #{shopId}


        and type = #{type}


        and title = #{title}


        and target_money = #{targetMoney}


        and discount_money = #{discountMoney}


        and end_time = #{endTime}


        and add_time = #{addTime}


        and number = #{number}


        and status = #{status}
