mapper.select:
select
id, user_id, shop_id, total_money, discount_money, pay_money, add_time
from cart


        and id = #{id}


        and user_id = #{userId}


        and shop_id = #{shopId}


        and total_money = #{totalMoney}


        and discount_money = #{discountMoney}


        and pay_money = #{payMoney}


        and add_time = #{addTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from cart


        and id = #{id}


        and user_id = #{userId}


        and shop_id = #{shopId}


        and total_money = #{totalMoney}


        and discount_money = #{discountMoney}


        and pay_money = #{payMoney}


        and add_time = #{addTime}
