mapper.select:
select
id, cart_id, food_id, number, add_time
from cart_info


        and id = #{id}


        and cart_id = #{cartId}


        and food_id = #{foodId}


        and number = #{number}


        and add_time = #{addTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from cart_info


        and id = #{id}


        and cart_id = #{cartId}


        and food_id = #{foodId}


        and number = #{number}


        and add_time = #{addTime}
