mapper.select:
select count(1)
from order_comment


        and id = #{id}


        and order_id = #{orderId}


        and user_id = #{userId}


        and content = #{content}


        and images = #{images}


        and add_time = #{addTime}


        and path = #{path}


        and num_praise = #{numPraise}


        and status = #{status}


        and re_comment_id = #{reCommentId}


        and like_ids = #{likeIds}
