mapper.select:
select
id, username, mobile, password, open_id, wallet, email, truename, gender, status, add_time
from user


        and id = #{id}


        and username = #{username}


        and mobile = #{mobile}


        and password = #{password}


        and open_id = #{openId}


        and wallet = #{wallet}


        and email = #{email}


        and truename = #{truename}


        and gender = #{gender}


        and status = #{status}


        and add_time = #{addTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and id = #{id}


        and username = #{username}


        and mobile = #{mobile}


        and password = #{password}


        and open_id = #{openId}


        and wallet = #{wallet}


        and email = #{email}


        and truename = #{truename}


        and gender = #{gender}


        and status = #{status}


        and add_time = #{addTime}
