mapper.select:
select
id, user_id, username, gender, mobile, province, city, district, longitude, latitude, address, street, tag,
defaultAddress, status, add_time, edit_time
from user_address


        and id = #{id}


        and user_id = #{userId}


        and username = #{username}


        and gender = #{gender}


        and mobile = #{mobile}


        and province = #{province}


        and city = #{city}


        and district = #{district}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and address = #{address}


        and street = #{street}


        and tag = #{tag}


        and defaultAddress = #{defaultaddress}


        and status = #{status}


        and add_time = #{addTime}


        and edit_time = #{editTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user_address


        and id = #{id}


        and user_id = #{userId}


        and username = #{username}


        and gender = #{gender}


        and mobile = #{mobile}


        and province = #{province}


        and city = #{city}


        and district = #{district}


        and longitude = #{longitude}


        and latitude = #{latitude}


        and address = #{address}


        and street = #{street}


        and tag = #{tag}


        and defaultAddress = #{defaultaddress}


        and status = #{status}


        and add_time = #{addTime}


        and edit_time = #{editTime}
